  `data_country_code` varchar(4) COLLATE utf8_unicode_ci NOT NULL,
  `data_browser` varchar(45) COLLATE utf8_unicode_ci NOT NULL,
  `data_browser_version` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
